.control:focus ~ .floating-label,
.control:not(:focus):valid ~ .floating-label{
  top: 6px;
  left: 5px;
  font-size: 11px;
  opacity: 0.6;
}

.control {
  font-size: 14px;
  width: 200px;
  height: 45px;
}

.floating-label {
  position: absolute;
  pointer-events: none;
  left: 20px;
  top: 14px;
  transition: 0.3s ease all;
