File `index.js`:

```js
'use strict';

var express = require('express');
var app = express();
var nunjucks = require('nunjucks');
var path = require('path');

const VIEW_PATH = path.join(__dirname, 'views');

// configure Nunjucks templating
nunjucks.configure(
    VIEW_PATH, // tell nunjucks, that all views are in views/ folder
    {express: app} // tell nunjucks, that this is express app
);

// set view engine to custom extension - njk
app.set('view engine', 'njk');

app
    .use('*.html', function (req, res) { // render all html files with Nunjucks

        // show original URL in the console
        console.log('Original URL: %s', req.originalUrl);

        // generate path to view
        let view_path = path.join(VIEW_PATH, req.originalUrl);

        // replace *.html to *.njk
        view_path = view_path.replace(/\..+/, '.njk');

        console.log('File to render: %s', view_path);

        // render template
        res.render(view_path);
    })
    .get('/', function (req, res) {
        // for home page just show this HTML
        res.send('<h1>Home Page</h1>');
    })
    .get('/patient/:id?', function (req, res) {
        // generate path to the specific view
        let view_path = path.join(VIEW_PATH, 'patient.njk');

        // pass id parameter to the view as patient_id variable
        res.render(view_path, {patient_id: req.params.id});
    })
    .listen(3003);
