<?php
class DbMessageSource extends CDbMessageSource
{
	/**
	 * @inheritdoc
	 */
	public function loadMessages($category,$language)
	{
		return parent::loadMessages($category,$language);
	}

	/**
	 * @inheritdoc
	 */
	public function loadMessagesFromDb($category,$language)
	{
		$rows    =$this->getDbConnection()->createCommand("
			select message, translation
			from {$this->sourceMessageTable} t1, {$this->translatedMessageTable} t2
			where t1.id=t2.id and category=:category and language=:language")
			->queryAll(true,[':category'=>$category,':language'=>$language]);
		$messages=[];
		foreach($rows as $row)
			$messages[$row['MESSAGE']]=$row['TRANSLATION'];

		return $messages;
	}
}
