Log route implementation:
```php
<?php

class ELogDb extends CDbLogRoute
{
	protected function createLogTable($db,$tableName)
	{
		// create table and sequence
		$sql="CREATE TABLE {$this->logTableName} (
						ID       NUMBER(18),
						LEVEL_   VARCHAR2(128),
						CATEGORY VARCHAR2(128),
						LOGTIME  DATE,
						IP       VARCHAR2(50),
						USERID   NUMBER(18),
						USERNAME VARCHAR2(50),
						URL      VARCHAR2(1000),
						MESSAGE  CLOB
					)";
		Yii::app()->odb->exec($sql);
		$sql="CREATE SEQUENCE SEQ_{$this->logTableName} MINVALUE 1 MAXVALUE 999999999999999999 START WITH 1 INCREMENT BY 1 CYCLE ORDER";
		Yii::app()->odb->exec($sql);
	}

	protected function processLogs($logs)
	{
		foreach($logs as $log)
		{
			$v_get ='$_GET:'.print_r($_GET,true);
			$v_post='$_POST:'.print_r($_POST,true);
			$msg   =implode("\n\n\n",[$log[0],$v_get,$v_post]);
			Yii::app()->odb->exec('begin logger.log(:pLevel,:pCategory,:pIp,:pUser,:pUsername,:pUrl,:pMsg); end;',[
				':pLevel'   =>$log[1],
				':pCategory'=>$log[2],
				':pIp'      =>Yii::app()->request->userHostAddress,
				':pUser'    =>Yii::app()->user->id,
				':pUsername'=>Yii::app()->user->name,
				':pUrl'     =>Yii::app()->request->url,
				':pMsg'     =>[$msg,OCI_B_CLOB,-1,false],
			]);
		}
	}
}
