Export data into CSV in UTF-8 encoding
===

```php
<?php
class Csver extends CComponent
{
	private $options;
	private $fileName;
	private $url;

	function __construct(array $options)
	{
		$this->options =$options;
		$dir           ='uploads/export/'; // dir to export CSV file
		$tmpFilename   =@tempnam($dir,'export'); // create temp file
		$this->fileName=$tmpFilename.".csv"; // add extension
		rename($tmpFilename,$this->fileName);
		$this->url=$dir.basename($this->fileName); // generate URL to download a file
	}

	/**
	 * Adds special symbols to represent value as text.
	 * @param string $val value
	 * @return string formatted value
	 */
	private function asText($val) { return '="'.$val.'"'; }

	/**
	 * Exports data to CSV.
	 * @param array  $input_array data to be exported.
	 * @param string $delimiter   delimiter, default ';'.
	 * @param bool   $download    false - return url and path, true - force download.
	 * @return array url and path of the generated file as [path=>xxx,url=>yyy]
	 */
	public function export($input_array,$delimiter=';',$download=false)
	{
		// open temporary file
		$fp=fopen($this->fileName,'w');

		// write BOM into UTF-8 encoded file to properly display characters
		fwrite($fp,pack("CCC",0xef,0xbb,0xbf));

		// convert to right encoding
		foreach($input_array as &$row)
		{
			$idx=0;
			foreach($row as $key=>$val)
			{
				if(is_numeric($key))
					$row[$key]=$this->asText($val);
				else
				{
					if($this->options['header'][$idx]['type']=='text')
						$row[$key]=$this->asText($row[$key]);
				}
				$idx++;
			}
		}

		// put header
		if(!empty($this->options['header']))
		{
			$col_keys=array_keys($input_array[0]);
			$idx     =0;
			foreach($this->options['header'] as $hdr)
			{
				$header[$col_keys[$idx]]=isset($hdr['title'])?$hdr['title']:'';
				$idx++;
			}
			array_unshift($input_array,$header);
		}
		// write as CSV
		foreach($input_array as $line)
			fputcsv($fp,$line,$delimiter);

		// close file
		fclose($fp);

		if(!$download)
			// return info
			return ['file'=>$this->fileName,'url'=>$this->url,];
		else
		{
			// open temporary file
			$fp=fopen($this->fileName,'rb');

			// modify header to be downloadable csv file
			header('Content-Type: application/csv');
			header('Content-Disposition: attachement; filename="'.basename($this->fileName).'";');

			// Send file to browser for download
			fpassthru($fp);
		}
	}
}
```

