<?php

class DoriUz
{
	const BASE_URL='http://www.doridarmon.uz/api/';

	protected $limit;
	protected $page;

	function __construct($limit=20,$page=1)
	{
		$this->limit=$limit;
		$this->page =$page;
	}

	protected function isJson($string)
	{
		json_decode($string);
		return (json_last_error()==JSON_ERROR_NONE);
	}

	public function search($name)
	{
		$result=[];
		if(isset($name))
		{
			$curl=curl_init();
			$url =self::BASE_URL.http_build_query([
					'R'     =>'search',
					'limit' =>$this->limit,
					'page'  =>$this->page,
					'search'=>$name,
				]);

			curl_setopt_array($curl,[
				CURLOPT_URL           =>$url,
				CURLOPT_HTTPHEADER    =>['Content-Type: application/json; charset=utf-8'],
				CURLOPT_RETURNTRANSFER=>true,
			]);
			$result=curl_exec($curl);

			if(!$this->isJson($result))
				return $result;

			$err=curl_error($curl);
			if($err)
				return $err;

			$result=json_decode($result,true);
		}
		return $result;
	}
}

