Application component to work with Oracle DB via OCI
===

Component implementation
=====

```php
<?php

class EOciConnection extends CApplicationComponent
{
	public $connectionString;
	public $username;
	public $password;
	public $charset;
	public $conn;
	public $stid;
	public $persistent;

	public function init()
	{
		if(!$this->persistent)
			$this->conn=oci_connect($this->username,$this->password,$this->connectionString,$this->charset);
		else
			$this->conn=oci_pconnect($this->username,$this->password,$this->connectionString,$this->charset);
		return $this;
	}

	public function close()
	{
		if(!is_null($this->stid)) oci_free_statement($this->stid);
		if($this->conn) oci_close($this->conn);
	}

	/**
	 * @param string $sql         SQL statement. Maybe with placeholders, such as :par1, :par2.
	 * @param array  $params      parameters, such as [':par1'=>val1,':par2'=>val2].
	 * @param bool   $fetch_first false-fetch all rows, true-fetch only first row.
	 * @return array fetched rows.
	 */
	public function select($sql,$params=[],$fetch_first=false,$fetch_mode=OCI_ASSOC)
	{
		$rows      =[];
		$this->stid=oci_parse($this->conn,$sql);
		if(!empty($params))
			foreach($params as $param_name=>$param_value)
				oci_bind_by_name($this->stid,$param_name,$params[$param_name]);
		oci_execute($this->stid);
		if($fetch_first)
			$rows=oci_fetch_array($this->stid,$fetch_mode);
		else
			oci_fetch_all($this->stid,$rows,0,-1,OCI_FETCHSTATEMENT_BY_ROW+$fetch_mode);
		return $rows;
	}

	/**
	 * @param string $sql    SQL statement to execute
	 * @param array  $params key/value pairs for simple parameters or array of parameters in [param-value,param-type,param-length,is-out-param] format.
	 * @return array|bool
	 */
	public function exec($sql,$params=[])
	{
		$outs      =[];
		$lobs      =[];
		$this->stid=oci_parse($this->conn,$sql);
		if(!empty($params))
			foreach($params as $param_name=>$param_value)
				if(is_array($param_value))
				{
					list($value,$param_type,$param_length,$is_out)=$param_value;
					if($param_type==OCI_B_CLOB && !$is_out)
					{
						$lobs[$param_name]=oci_new_descriptor($this->conn,OCI_DTYPE_LOB);
						$lobs[$param_name]->setBuffering(false);
						oci_bind_by_name($this->stid,$param_name,$lobs[$param_name],-1,OCI_B_CLOB);
						$lobs[$param_name]->writetemporary($value);
					}
					elseif($param_type==OCI_B_BLOB && !$is_out)
					{
						$lobs[$param_name]=oci_new_descriptor($this->conn,OCI_DTYPE_LOB);
						$lobs[$param_name]->setBuffering(false);
						oci_bind_by_name($this->stid,$param_name,$lobs[$param_name],-1,OCI_B_BLOB);
						$lobs[$param_name]->writetemporary($value,OCI_TEMP_BLOB);
					}
					else
					{
						if($is_out)
						{
							$value_name=trim($param_name,':');
							if($param_type==OCI_B_CLOB || $param_type==OCI_B_BLOB)
							{
								$$value_name=oci_new_descriptor($this->conn,OCI_DTYPE_LOB);
								oci_bind_by_name($this->stid,$param_name,$$value_name,-1,$param_type);
							}
							else
								oci_bind_by_name($this->stid,$param_name,$$value_name,$param_length>0?$param_length:-1,$param_type);
							$outs[$value_name]='';
						}
						else
						{
							oci_bind_by_name($this->stid,$param_name,$value,$param_length>0?$param_length:-1,$param_type);
						}
					}
				}
				else oci_bind_by_name($this->stid,$param_name,$params[$param_name]);
		$res_875785785_fhfhj=oci_execute($this->stid,OCI_NO_AUTO_COMMIT);
		if(!empty($outs))
		{
			foreach($outs as $name=>$val)
				if(gettype($$name)=="object" && get_class($$name)=='OCI-Lob')
					$outs[$name]=$$name->load();
				else
					$outs[$name]=$$name;
			return $outs;
		}
		return $res_875785785_fhfhj;
	}

	/**
	 * Executes SQL statements which return sys_refcursor object.
	 * @param string $sql    SQL statement to execute
	 * @param array  $params key/value pairs for simple parameters or array of parameters in [param-value,param-type,param-length,is-out-param] format.
	 * @return array if there is OUT parameters, then returns ['rows'=>[...],'outs'=>[...]] array, otherwise returns cursor rows.
	 */
	public function cursor($sql,$params=[])
	{
		$this->stid=oci_parse($this->conn,$sql);
		if(!empty($params))
		{
			$idx=0;
			foreach($params as $param_name=>$param_value)
			{
				if(!$idx)
				{
					$cur=oci_new_cursor($this->conn);
					oci_bind_by_name($this->stid,$param_name,$cur,-1,OCI_B_CURSOR);
				}
				elseif(is_array($param_value))
				{
					list($value,$param_type,$param_length,$is_out)=$param_value;
					if($param_type==OCI_B_CLOB)
					{
						$clob=oci_new_descriptor($this->conn,OCI_DTYPE_LOB);
						oci_bind_by_name($this->stid,$param_name,$clob,-1,OCI_B_CLOB);
						$clob->writetemporary($value);
					}
					elseif($param_type==OCI_B_BLOB)
					{
						$blob=oci_new_descriptor($this->conn,OCI_DTYPE_LOB);
						oci_bind_by_name($this->stid,$param_name,$blob,-1,OCI_B_BLOB);
						$blob->writetemporary($value,OCI_TEMP_BLOB);
					}
					else
					{
						if($is_out)
						{
							$value_name=trim($param_name,':');
							oci_bind_by_name($this->stid,$param_name,$$value_name,$param_length>0?$param_length:-1,$param_type);
							$outs[$value_name]='';
						}
						else oci_bind_by_name($this->stid,$param_name,$value,$param_length>0?$param_length:-1,$param_type);
					}
				}
				else
					oci_bind_by_name($this->stid,$param_name,$params[$param_name]);
				++$idx;
			}
			oci_execute($this->stid,OCI_NO_AUTO_COMMIT);
			if(!empty($outs))
			{
				foreach($outs as $name=>$val)
					if(gettype($$name)=="object" && get_class($$name)=='OCI-Lob')
						$outs[$name]=$$name->load();
					else
						$outs[$name]=$$name;
			}
			oci_execute($cur);
			oci_fetch_all($cur,$rows,0,-1,OCI_FETCHSTATEMENT_BY_ROW+OCI_ASSOC);
			if(!empty($outs))
				return ['rows'=>$rows,'outs'=>$outs];
		}
		return $rows;
	}

	public function commit()
	{
		oci_commit($this->conn);
	}

	public function rollback()
	{
		oci_rollback($this->conn);
	}
}
```

