Transaction example in Yii2
===

```php
<?php
public function actionSave()
{
    $set=Yii::$app->request->post('Setting');
    if(isset($set['id']))
    {
        Yii::$app->db->transaction(function (Connection $db) use ($set)
        {
            $model    =$this->findModel($set['id']);
            $old_model=clone $model;
            if(isset($set['conf_value'])) $model->conf_value=$set['conf_value'];
            if(isset($set['status'])) $model->status=$set['status'];
            if($old_model->conf_value!=$model->conf_value || $old_model->status!=$model->status)
            {
                $user_id=Yii::$app->user->isGuest?1:Yii::$app->user->id; // #todo: remove guest after RBAC implementation
                $db->createCommand()->update(Setting::tableName(),['conf_value'=>$model->conf_value,'status'=>$model->status],['id'=>$model->id])->execute();
                $db->createCommand()->insert(SettingHistory::tableName(),[
                    'conf_id'    =>$old_model->id,
                    'conf_value' =>$old_model->conf_value,
                    'conf_status'=>$old_model->status,
                    'conf_date'  =>new Expression('NOW()'),
                    'user_id'    =>$user_id,
                ])->execute();
                F::resp(200,'Saved!');
            }
            else F::resp(200,'Nothing changed.');
        });
    }
    else F::resp(404,'Setting not found.');
}
