	private function setProperties()
	{
		if(!empty($this->options['properties']) && is_array($this->options['properties']))
		{
			$prop_mngr=$this->excel->getProperties();
			foreach($this->options['properties'] as $prop=>$val)
			{
				$method='set'.ucfirst($prop);
				if(method_exists($prop_mngr,$method))
					$prop_mngr->{$method}($val);
			}
		}
		return $this;
	}

	private function setHeaders()
	{
		if(is_array($this->header) && !empty($this->header))
		{
			$this->idxCol=0;
			foreach($this->header as $colName)
				$this->sheet->setCellValueExplicitByColumnAndRow($this->idxCol++,$this->idxRow,$colName);
			$this->idxRow++;
		}
		if($this->firstRowHeader)
		{
			$this->idxCol=0;
			foreach($this->data[0] as $colName=>$colValue)
				$this->sheet->setCellValueExplicitByColumnAndRow($this->idxCol++,$this->idxRow,$colName);
			$this->idxRow++;
		}
		return $this;
	}

	private function setRows()
	{
		foreach($this->data as $row)
		{
			$this->idxCol=0;
			foreach($row as $col=>$val)
				$this->sheet->setCellValueExplicitByColumnAndRow($this->idxCol++,$this->idxRow,$val);
			$this->idxRow++;
		}
		return $this;
	}

	private function genFileName()
	{
		$dir_def       ='files';
		$dir_          =isset($this->options['save']['dir'])?trim($this->options['save']['dir']):"$dir_def";
		$dir_          =$this->endsWith($dir_,'/')?$dir_:$dir_.'/';
		$pfx_          =isset($this->options['save']['prefix'])?$this->options['save']['prefix']:'export';
		$ext_          =isset($this->options['save']['ext'])?$this->options['save']['ext']:'xlsx';
		$url_          =isset($this->options['save']['url'])?$this->options['save']['url']:$dir_;
		$url_          =$this->endsWith($url_,'/')?$url_:$url_.'/';
		$tmpFilename   =@tempnam($dir_,$pfx_);
		$this->fileName=$tmpFilename.".$ext_";
		rename($tmpFilename,$this->fileName);
		$this->url=$url_.basename($this->fileName);
		return $this;
	}

	private function save()
	{
		$this->genFileName();
		$writer=PHPExcel_IOFactory::createWriter($this->excel,'Excel2007');
		if($this->download)
		{
			$fileName=basename($this->fileName);
			// Redirect output to a clientâs web browser (Excel2007)
			header('Content-Type: application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');
			header('Content-Disposition: attachment;filename="'.$fileName.'"');
			header('Cache-Control: max-age=0');
			// If you're serving to IE 9, then the following may be needed
			//header('Cache-Control: max-age=1');
			// If you're serving to IE over SSL, then the following may be needed
			//header('Expires: Mon, 26 Jul 1997 05:00:00 GMT'); // Date in the past
			//header('Last-Modified: '.gmdate('D, d M Y H:i:s').' GMT'); // always modified
			//header('Cache-Control: cache, must-revalidate'); // HTTP/1.1
			//header('Pragma: public'); // HTTP/1.0
			$writer->save('php://output');
			exit;
		}
		else
			$writer->save($this->fileName);
		return $this;
	}

	public function setActiveSheetIdx($idx=0)
	{
		$this->sheet =$this->excel->setActiveSheetIndex($idx);
		$this->idxRow=1;
		$this->idxCol=0;
		return $this;
	}

	public function export()
	{
		if(is_array($this->data) && !empty($this->data))
		{
			$this->setProperties()
				->setActiveSheetIdx()
				->setHeaders()
				->setRows()
				->save();
			return [
				'file'=>$this->fileName,
				'url' =>$this->url,
			];
		}
		else
			Yii::log('Data is not array or is empty. data:'.print_r($this->data,true));
		return false;
	}
}
