	angular.module('MyApp',['toaster'])
	.config(function($httpProvider){
		$httpProvider.interceptors.push(function($q,$rootScope){
			return {
				'response':function(response){
					// intercept response event & broadcast loading-complete event with response as parameter
					$rootScope.$broadcast('loading-complete',response);
					return response || $q.when(response);
				}
			};
		});
	})
	.directive('autoToast',function(toaster){
