`app.js`
```js
angular.module('App',['ui.router'])
	.factory('MyFactory',function($http){
		return {
			common:{
				countries:function(){
					return $http.post('/scripts/countries.php');
				},
				cities   :function(){
					return $http.post('/scripts/cities.php');
				}
			}
		}
	})
	.config(function($stateProvider,$urlRouterProvider){
		$urlRouterProvider.otherwise('/');
		$stateProvider
			.state('parent',{ // abstract parent state for all states
				url       :'/parent',
				abstract  :true,
				template  :'<ui-view>', // important!
				resolve   :{
					data:function($q,Data){
						var def=$q.defer();
						// both are promises
						var c1=Data.common.countries();
						var c2=Data.common.cities();
						// just to see when data loaded:
						c1.then(function(resp){console.log('Countries loaded:',resp);});
						c2.then(function(resp){console.log('Cities loaded:',resp);});
						// resolve when all data loaded
						$q.all([c1,c2]).then(function(resp){
							console.log('All data loaded:',resp);
							def.resolve(resp);
						});
						return def.promise;
					}
				},
				controller:function($scope,data){
					console.log('Parent controller:',data);
					// available in all scopes
					$scope.common.countries=data[0].data;
					$scope.common.cities=data[1].data;
				}
			})
			.state('parent.child',{
				url        :'/child',
				resolve    :{data:function(data){ return data; }},
				templateUrl:'partials/child.html',
				controller :'MyCtrl'
			});
	})
	.controller('MyCtrl',function($scope){
		console.log('Child controller');
		console.log('Countries:',$scope.common.countries);
		console.log('Cities:',$scope.common.cities);
	});
