<?php

/**
 * The followings are the available columns in table 'params':
 * @property string $param_type
 * @property string $param_name
 * @property string $param_value
 * @property string $param_description
 */
class Params extends CActiveRecord
{
	public function tableName()
	{
		return 'params';
	}

	public static function model( $className = __CLASS__ )
	{
		return parent::model( $className );
	}

	/**
	 * Get parameter types as array.
	 * @return array parameter types.
	 */
	public static function types()
	{
		$criteria    = new CDbCriteria( [ 'select' => 'param_type', 'distinct' => true ] );
		$param_types = CHtml::listData( self::model()->findAll( $criteria ), 'param_type', 'param_type' );
		return array_values( $param_types );
	}

	/**
	 * Get parameter values as array.
	 * @param string $paramType
	 * @return array parameter values.
	 */
	public static function values( $paramType )
	{
		$values = self::model()->findAll( [ 'condition' => 'param_type=:param_type', 'params' => [ ':param_type' => $paramType ] ] );
		return CHtml::listData( $values, 'param_value', 'param_name' );
	}

	/**
	 * Get parameter value by type and name.
	 * @param string $paramType type of the parameter
	 * @param string $paramName name of the parameter
	 * @return mixed parameter value or null, if no such parameter exist.
	 */
	public static function value( $paramType, $paramName )
	{
		return self::model()->find( [
			'condition' => 'param_type=:param_type AND param_name=:param_name',
			'params'    => [
				':param_type' => $paramType,
				':param_name' => $paramName,
			], ] )->param_value;
	}
}
```

