{
	var trans={
		'Ð°': 'a', 'Ð±': 'b', 'Ð²': 'v', 'Ð³': 'g', 'Ð´': 'd', 'Ðµ': 'e', 'Ñ': 'jo', 'Ð¶': 'zh', 'Ð·': 'z', 'Ð¸': 'i', 'Ð¹': 'jj',
		'Ðº': 'k', 'Ð»': 'l', 'Ð¼': 'm', 'Ð½': 'n', 'Ð¾': 'o', 'Ð¿': 'p', 'Ñ': 'r', 'Ñ': 's', 'Ñ': 't', 'Ñ': 'u', 'Ñ': 'f',
		'Ñ': 'kh', 'Ñ': 'c', 'Ñ': 'ch', 'Ñ': 'sh', 'Ñ': 'shh', 'Ñ': '', 'Ñ': 'y', 'Ñ': '', 'Ñ': 'eh', 'Ñ': 'ju', 'Ñ': 'ja'
